import React from 'react';
import Sidebar from 'react-sidebar';
import Toolbar from 'material-ui/lib/toolbar/toolbar';
import ToolbarTitle from 'material-ui/lib/toolbar/toolbar-title';
import IconButton from 'material-ui/lib/icon-button';
import ToolbarGroup from 'material-ui/lib/toolbar/toolbar-group';
import Menu from 'material-ui/lib/menus/menu';
import MenuItem from 'material-ui/lib/menus/menu-item';
import FontIcon from 'material-ui/lib/font-icon';

import DiscordWidget from './DiscordWidget';

class Drawer extends React.Component {
  static propTypes = {
    isOpen: React.PropTypes.bool.isRequired,
    isDocked: React.PropTypes.bool.isRequired,
    toggleDrawer: React.PropTypes.func.isRequired,
    dockDrawer: React.PropTypes.func,
    undockDrawer: React.PropTypes.func,
    children: React.PropTypes.array
  };

  static contextTypes = {
    muiTheme: React.PropTypes.object
  };

  styles () {
    return {
      link: {
        textDecoration: 'none'
      },
      toolbar:  {
        display: 'flex',
        padding: '0 1rem',
        backgroundColor: this.context.muiTheme.rawTheme.palette.accent1Color,
        justifyContent: 'space-between'
      },
      toolbarGroup: {
        alignItems: 'center',
        display: 'flex'
      },
      iconButton: {
        margin: 'auto'
      },
      iconButtonIcon: {
        color: `#ffffff`
      },
      drawer: {
        backgroundColor: '#ffffff'
      },
      content: {
        display: 'flex',
        flexDirection: 'column'
      },
      badButton: {
        padding: 'initial',
        border: 'initial',
        backgroundColor: 'initial',
        textAlign: 'left',
        width: '100%'
      },
      white: {
        color: `#ffffff`
      },
      icon:  {
        fill: '#ffffff'
      }
    };
  }

  render () {
    return (
      <Sidebar styles={{sidebar: this.styles().drawer, content: this.styles().content}}
        sidebar={
          <div>
            <Toolbar style={this.styles().toolbar} noGutter>
              <ToolbarGroup style={this.styles().toolbarGroup}>
                <ToolbarTitle style={this.styles().white}
                  text={this.props.isDocked ? 'Undock Drawer' : 'Dock Drawer'} />
              </ToolbarGroup>
              <ToolbarGroup style={this.styles().toolbarGroup}>
                <IconButton iconClassName="material-icons" style={this.styles().iconButton}
                  iconStyle={this.styles().iconButtonIcon}
                  onClick={this.props.isDocked ? this.props.undockDrawer : this.props.dockDrawer}
                >
                  {this.props.isDocked ? 'fullscreen_exit' : 'fullscreen'}
                </IconButton>
              </ToolbarGroup>
            </Toolbar>
            <Menu>
              <MenuItem primaryText="Download Client"
                href="https://github.com/nictuku/stardew-rocks"
                leftIcon={<FontIcon className="material-icons">file_download</FontIcon>}
              />
              <form action="https://www.paypal.com/cgi-bin/webscr" method="post" target="_top">
                <input type="hidden" name="cmd" value="_s-xclick" />
                <input type="hidden" name="encrypted" value="-----BEGIN PKCS7----------END PKCS7-----
                " />
                <button type="submit" style={this.styles().badButton}>
                  <MenuItem primaryText="Donate"
                    leftIcon={<object style={this.styles().icon} type="image/svg+xml" data="content/paypal.svg" />}
                  />
                </button>
              </form>
              <DiscordWidget />
            </Menu>
          </div>
        }
        open={this.props.isOpen}
        docked={this.props.isDocked}
        onSetOpen={this.props.toggleDrawer}
      >
        {this.props.children}
      </Sidebar>
    );
  }
}

export default Drawer;
